<script>
function toggleLanguageDropdown(prefix) {
    const dropdown = document.getElementById('language-dropdown-' + prefix);
    const toggle = dropdown.previousElementSibling;
    const chevron = toggle.querySelector('.chevron-down');

    const isOpen = dropdown.style.display === 'block';

    document.querySelectorAll('[id^="language-dropdown-"]').forEach(d => {
        if (d !== dropdown) {
            d.style.display = 'none';
            const otherToggle = d.previousElementSibling;
            const otherChevron = otherToggle.querySelector('.chevron-down');
            otherToggle.setAttribute('aria-expanded', 'false');
            if (otherChevron) {
                otherChevron.style.transform = 'rotate(0deg)';
            }
        }
    });

    dropdown.style.display = isOpen ? 'none' : 'block';
    toggle.setAttribute('aria-expanded', !isOpen);

    if (chevron) {
        chevron.style.transform = isOpen ? 'rotate(0deg)' : 'rotate(180deg)';
    }
}

function changeLanguage(lang) {
    const form = document.createElement('form');
    form.method = 'POST';
    form.action = '/change-language';
    form.style.display = 'none';

    const csrfToken = document.querySelector('meta[name="csrf-token"]');
    if (csrfToken) {
        const csrfInput = document.createElement('input');
        csrfInput.type = 'hidden';
        csrfInput.name = '_token';
        csrfInput.value = csrfToken.getAttribute('content');
        form.appendChild(csrfInput);
    }

    const langInput = document.createElement('input');
    langInput.type = 'hidden';
    langInput.name = 'language';
    langInput.value = lang;
    form.appendChild(langInput);

    document.body.appendChild(form);
    form.submit();
}

document.addEventListener('click', function(e) {
    if (!e.target.closest('.language-switcher')) {
        document.querySelectorAll('[id^="language-dropdown-"]').forEach(dropdown => {
            dropdown.style.display = 'none';
            const toggle = dropdown.previousElementSibling;
            const chevron = toggle.querySelector('.chevron-down');
            toggle.setAttribute('aria-expanded', 'false');
            if (chevron) {
                chevron.style.transform = 'rotate(0deg)';
            }
        });
    }
});
</script>
